# ========= DECORATORS ===========

def owner_filter(func):
    async def wrapper(client, message):
        if message.from_user.id == OWNER_ID:
            return await func(client, message)
        else:
            await message.reply_text("‚ùå Only the owner can use this command.")
    return wrapper

def auth_user_filter(func):
    async def wrapper(client, message):
        user_id = message.from_user.id
        if user_id == OWNER_ID or users_collection.find_one({"user_id": user_id}):
            return await func(client, message)
        else:
            await message.reply_text("‚ùå You are not authorized to use this command.")
    return wrapper

