# 1. ADD USER
@bot.on_message(filters.command("adduser") & filters.private)
@owner_filter
async def add_user(client, message: Message):
    if len(message.command) < 2:
        await message.reply_text("Usage: `/adduser user_id`", quote=True)
        return
    try:
        user_id = int(message.command[1])
    except ValueError:
        await message.reply_text("‚ùå Invalid user ID.", quote=True)
        return

    if users_collection.find_one({"user_id": user_id}):
        await message.reply_text("‚úÖ User already authorized.", quote=True)
    else:
        users_collection.insert_one({"user_id": user_id})
        await message.reply_text("‚úÖ User added successfully.", quote=True)
        try:
            await client.send_message(user_id, "‚úÖ You have been authorized!")
        except Exception as e:
            print(f"Failed to notify user: {e}")

